mapper.select:
SELECT a.id, a.no, a.createDate, a.title, u.name, COUNT(c.id) AS commentCount FROM article a INNER JOIN user u ON a.userId = u.id LEFT JOIN comment c ON a.id = c.articleId
INNER JOIN board b ON a.boardId = b.id WHERE b.id = #{boardId}
GROUP BY a.id ORDER BY a.createDate desc;

SELECT `no` FROM article
WHERE boardId = #{boardId}
ORDER BY no desc
LIMIT 0, 1;

SELECT DISTINCT a.createDate, u.name as userName, a.id, title, body FROM article a

    inner join board b ON a.boardId = #{boardId}

LEFT JOIN user u ON a.userId = u.id


        AND a.boardId = #{boardId}


        AND INSTR(title, #{qr}) > 0
        OR INSTR(body, #{qr}) > 0


        AND INSTR(title, #{qr}) > 0


        AND u.id IN

            #{id}



ORDER BY a.createDate DESC

mapper.update:
UPDATE article SET title = #{title}, body = #{body},
boardId = #{boardId}, `no` = #{no},
modifiedDate = now() WHERE id = #{articleId}
